var assert = require('assert');
var fs = require('fs');
var path = require('path');
var AzureMongoEndpoint = require('azureMongoEndpoints').AzureMongoEndpoint;
var topologyChangeEvents = 0;

var keepEventLoop = fs.openSync('event_loop_file', 'w');

prepare({ 'instances': [ {'host':'127.255.0.0','port':27017} ] });

var endPoint = new AzureMongoEndpoint('TestRole', 'TestPort');
assert.equal(0, endPoint.instances.length);

endPoint.on('topologyChange', function() {
  if (topologyChangeEvents == 0) {
    assert.equal(1, endPoint.instances.length);
    assert.equal('127.255.0.0', endPoint.instances[0].host);
    assert.equal(27017, endPoint.instances[0].port);

    fs.writeFileSync(
      'testEndpoints/approot/azureMongoEndpoints/TestRole/TestPort.json',
      JSON.stringify({ 'instances': [ {'host':'127.255.0.0','port':27017}, 
        {'host':'127.255.0.1','port':27018} ] }));
  } else {
    assert.equal(2, endPoint.instances.length);
    assert.equal('127.255.0.0', endPoint.instances[0].host);
    assert.equal(27017, endPoint.instances[0].port);
    assert.equal('127.255.0.1', endPoint.instances[1].host);
    assert.equal(27018, endPoint.instances[1].port);
    fs.closeSync(keepEventLoop);
  }

  topologyChangeEvents++;
});


function prepare(testEndpoints) {
  process.env.ROLEROOT = path.join(process.cwd(), 'testEndpoints');

  try {
    fs.mkdirSync('testEndpoints');
  } catch(err) {
  }

  try {
    fs.mkdirSync('testEndpoints/approot');
  } catch(err) {
  }

  try {
    fs.mkdirSync('testEndpoints/approot/azureMongoEndpoints');
  } catch(err) {
  }

  try {
    fs.mkdirSync('testEndpoints/approot/azureMongoEndpoints/TestRole');
  } catch(err) {
  }

  fs.writeFileSync(
    'testEndpoints/approot/azureMongoEndpoints/TestRole/TestPort.json',
    JSON.stringify(testEndpoints));
}

process.on('exit', function(code) {
  assert.equal(2, topologyChangeEvents);
  console.error('passed');
});